    if (flow.get("addNRrunning") == true) {
        msg.payload = "sudo fuser -k " + flow.get("freeport") + "/tcp"
        node.send([null, msg, null])
    }

    msg.payload = "[ -e ~/pekaway/nrbackups/" + flow.get("backupfile") + " ] && echo \"file found.\" || echo \"file unavailable\""
    return [msg, null, null];
}

msg.payload = "sudo fuser -k " + flow.get("freeport") + "/tcp"
flow.set("addNRrunning", false)
return msg;

msg.payload = msg.payload.replace(/\s/g, "").replace("1880", "")
if (msg.payload == "") {
    msg.payload = "No additional instance running"
}
else {
    msg.payload = "Additional instance port: " + msg.payload
}
return msg;

if (msg.payload.signal.includes("SIGKILL")) {
    return msg
}

let backupfile = flow.get("backupfile")

if (msg.payload.includes("found")) {
    flow.set("freeport", msg.freeport)
    msg.payload = "node-red ~/pekaway/nrbackups/" + backupfile + " -p " + msg.freeport
    flow.set("addNRrunning", true)
    return [null, msg]
}
else if (msg.payload.includes("unavailable")) {
    msg.payload = "File " + flow.get("backupfile") + "could not be found."
    return [msg, null]
}

if (msg.payload == "") { return null }
else {
    msg.payload = msg.payload.trim()
    return msg;
}

let pid = ""
if (msg.payload.includes("PID=")) {
    pid = msg.payload.substring(4, msg.payload.length)
    msg.payload = 'test -d /proc/' + pid + ' && echo \"process running\" || echo \"process does not exist\"'
    node.send([msg, null]);
    msg.payload = "Updatescript running (PID="+pid+")"
    return [null, msg]
}

if (msg.payload.includes("does not exist")) {
    msg.payload = "Update process done, pls check the logfile at /var/log/"
    msg.reset = true
    node.send([msg, null]);
    msg.payload = ""
    return [null, msg]
}

if (msg.payload.includes("PID=")) { return null }
else {
    return msg;
}

